CREATE OR REPLACE FUNCTION get_column_comment(p_table_name TEXT, p_column_name TEXT)
RETURNS TEXT AS $$
DECLARE
    column_comment TEXT;
BEGIN
    -- Abruf des Kommentars für die Spalte
    SELECT col_description(pg_attribute.attrelid, pg_attribute.attnum)
    INTO column_comment
    FROM pg_attribute
    JOIN pg_class ON pg_class.oid = pg_attribute.attrelid
    WHERE pg_class.relname = p_table_name
    AND pg_attribute.attname = p_column_name
    AND pg_attribute.attnum > 0; -- Ausschluss von Systemspalten

    -- Rückgabe des Kommentars oder eine Nachricht, wenn kein Kommentar vorhanden ist
    IF column_comment IS NOT NULL THEN
        RETURN column_comment;
    ELSE
        RETURN 'Kein Kommentar für diese Spalte gefunden';
    END IF;
END;
$$ LANGUAGE plpgsql;

