DROP TABLE IF EXISTS sensetivedata;
CREATE TABLE sensetivedata (
    name TEXT PRIMARY KEY
)

DO $$ 
DECLARE 
  stmt text;
BEGIN
  -- Übertrage Eigentum aller verbleibenden Objekte an postgres
  FOR stmt IN (
    SELECT 'ALTER ' || 
           CASE WHEN c.relkind = 'r' THEN 'TABLE' 
                WHEN c.relkind = 'S' THEN 'SEQUENCE' 
                WHEN c.relkind = 'v' THEN 'VIEW' 
                WHEN c.relkind = 'm' THEN 'MATERIALIZED VIEW' 
                WHEN c.relkind = 'f' THEN 'FOREIGN TABLE'
                WHEN c.relkind = 'p' THEN 'TABLE' 
           END || ' ' || quote_ident(nspname) || '.' || quote_ident(relname) || ' OWNER TO postgres;'
    FROM pg_class c
    JOIN pg_namespace n ON n.oid = c.relnamespace
    WHERE c.relowner = (SELECT oid FROM pg_roles WHERE rolname = 'Remi1.0worker')
  ) LOOP
    EXECUTE stmt;
  END LOOP;

  -- Entferne die Rolle aus allen Gruppen
  FOR stmt IN (
    SELECT 'REVOKE ' || quote_ident(r.rolname) || ' FROM "Remi1.0worker";'
    FROM pg_roles r
    JOIN pg_auth_members m ON r.oid = m.roleid
    WHERE m.member = (SELECT oid FROM pg_roles WHERE rolname = 'Remi1.0worker')
  ) LOOP
    EXECUTE stmt;
  END LOOP;

  -- Falls immer noch Objekte existieren, erzwinge deren Löschung
  FOR stmt IN (
    SELECT 'DROP OWNED BY "Remi1.0worker" CASCADE;'
  ) LOOP
    EXECUTE stmt;
  END LOOP;

  -- Jetzt die Rolle endgültig löschen
  EXECUTE 'DROP ROLE IF EXISTS "Remi1.0worker";';

END $$;
CREATE ROLE "Remi1.0worker" 
WITH 
  LOGIN 
  PASSWORD 'sicherespasswort';

GRANT CONNECT ON DATABASE "postgres" TO "Remi1.0worker";
GRANT USAGE ON SCHEMA public TO "Remi1.0worker";
GRANT SELECT, UPDATE, INSERT, DELETE ON ALL TABLES IN SCHEMA public TO "Remi1.0worker";
GRANT SELECT ON information_schema.columns TO "Remi1.0worker";

