// Zugriff auf die HTML-Daten aus der Eingabevariable
const htmlContent = $('HTTP Request5').first().json.data;

// Funktion zum Entfernen von HTML-Tags, JavaScript, CSS und Bereinigen des Textes
function cleanHtml(html) {
  // Entferne <script> und <style> Tags samt Inhalt
  html = html.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
  html = html.replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '');

  // Entferne alle HTML-Tags, behalte den Text dazwischen
  html = html.replace(/<[^>]+>/g, '');

  // Entferne überflüssige Leerzeichen, Tabs und Zeilenumbrüche
  html = html.replace(/\s+/g, ' ').trim();

  // Entferne HTML-Entities (z. B.  , &)
  html = html.replace(/&[a-zA-Z0-9#]+;/g, '');

  return html;
}

// Funktion zum Komprimieren und Filtern auf relevante Unternehmensinhalte
function compressBusinessContent(text) {
  // Liste von Stoppwörtern/Phrasen, die oft irrelevant sind
  const irrelevantPhrases = [
    'kontakt', 'impressum', 'datenschutz', 'agb', 'newsletter', 'login', 'registrieren',
    'folgen sie uns', 'social media', 'copyright', 'all rights reserved', 'sitemap'
  ];

  // Erweiterte Schlüsselwörter für Unternehmensaktivitäten, inkl. Bürospezifika und Akustik
  const businessKeywords = [
    'dienstleistungen', 'services', 'produkte', 'lösungen', 'angebot', 'portfolio',
    'automatisierung', 'unterstützung', 'support', 'technologie', 'entwicklung',
    'herstellung', 'verkauf', 'beratung', 'consulting', 'training', 'schulung',
    // Bürospezifische Begriffe
    'büro', 'arbeitsplatz', 'büroausstattung', 'möbel', 'ergonomie', 'büroplanung',
    'arbeitsumgebung', 'office', 'arbeitsstation', 'einrichtung', 'bürodesign',
    // Akustikbezogene Begriffe
    'akustik', 'schallschutz', 'schallabsorption', 'raumakustik', 'lärmschutz',
    'akustiklösungen', 'schalldämmung', 'akustikpaneele', 'schalloptimierung',
    // Allgemeine Lösungsbegriffe
    'optimierung', 'systeme', 'integration', 'effizienz', 'prozesse', 'management',
    'software', 'hardware', 'innovation', 'maßgeschneidert', 'lösungsanbieter',
    'qualität', 'zuverlässigkeit', 'nachhaltigkeit'
  ];

  // Text in Sätze aufteilen (nach Punkten, aber Leerzeichen beibehalten)
  const sentences = text.split('. ').map(s => s.trim());

  // Filtere Sätze:
  // - Behalte alle Sätze, die keine irrelevantPhrases enthalten
  const relevantSentences = sentences.filter(sentence => {
    const lowerSentence = sentence.toLowerCase();
    const hasIrrelevantPhrase = irrelevantPhrases.some(phrase => lowerSentence.includes(phrase));
    return !hasIrrelevantPhrase;
  });

  // Sortiere Sätze: Sätze mit businessKeywords zuerst
  const sortedSentences = relevantSentences.sort((a, b) => {
    const lowerA = a.toLowerCase();
    const lowerB = b.toLowerCase();
    const hasKeywordA = businessKeywords.some(keyword => lowerA.includes(keyword));
    const hasKeywordB = businessKeywords.some(keyword => lowerB.includes(keyword));
    return hasKeywordB - hasKeywordA; // Sätze mit Keywords nach vorne
  });

  // Verbinde die relevanten Sätze und begrenze die Länge (max. 5000 Zeichen)
  let compressedText = sortedSentences.join('. ').trim();
  if (compressedText.length > 5000) {
    compressedText = compressedText.substring(0, 4997) + '...';
  }

  // Falls kein relevanter Inhalt gefunden wurde, gib eine Standardmeldung zurück
  return compressedText || 'Keine spezifischen Informationen zu Unternehmensaktivitäten gefunden.';
}

// Bereinigten Text verarbeiten
const cleanedText = cleanHtml(htmlContent);

// Komprimierten Text mit Fokus auf Unternehmensinhalte erstellen
const compressedText = compressBusinessContent(cleanedText);

// Rückgabe im n8n-Format
return [{ json: { compressedText: compressedText } }];

// Zugriff auf die HTML-Daten aus der Eingabevariable
const htmlContent1 = $('HTTP Request2').first().json.data || '';
const htmlContent2 = $('HTTP Request3').first().json.data || '';

// Funktion zum Entfernen von HTML-Tags, JavaScript, CSS und Bereinigen des Textes
function cleanHtml(html) {
  // Entferne <script> und <style> Tags samt Inhalt
  html = html.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
  html = html.replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '');

  // Entferne alle HTML-Tags, behalte den Text dazwischen
  html = html.replace(/<[^>]+>/g, '');

  // Entferne überflüssige Leerzeichen, Tabs und Zeilenumbrüche
  html = html.replace(/\s+/g, ' ').trim();

  // Entferne HTML-Entities (z. B. &nbsp;, &amp;)
  html = html.replace(/&[a-zA-Z0-9#]+;/g, '');

  return html;
}

// Beide HTML-Inhalte bereinigen
const cleanedText1 = cleanHtml(htmlContent1);
const cleanedText2 = cleanHtml(htmlContent2);

// Zusammenfügen oder Standardtext, wenn beide leer sind
let combinedCleanedText = '';

if (cleanedText1 && cleanedText2) {
  combinedCleanedText = `${cleanedText1} ${cleanedText2}`;
} else if (cleanedText1) {
  combinedCleanedText = cleanedText1;
} else if (cleanedText2) {
  combinedCleanedText = cleanedText2;
} else {
  combinedCleanedText = 'keine telefonnummer gefunden';
}

// Rückgabe des kombinierten Textes im n8n-Format
return [{ json: { cleanedText: combinedCleanedText } }];
