const item1 = $("HTTP Request").first();
const item2 = $("Read/Write Files from Disk1").first();

if (!item1.binary || !item1.binary.data || !item2.binary || !item2.binary.data) {
  throw new Error('Ein oder beide Items enthalten keine Binärdaten!');
}

return [
  {
    json: {},
    binary: {
      angebot: {
        data: item1.binary.data.data,
        mimeType: item1.binary.data.mimeType || 'application/pdf',
        fileName: 'Angebot_pCon.pdf', // Explizit mit .pdf
        fileExtension: item1.binary.data.fileExtension || 'pdf',
        fileSize: item1.binary.data.fileSize || item1.binary.data.data.length
      },
      flyer: {
        data: item2.binary.data.data,
        mimeType: item2.binary.data.mimeType || 'application/pdf',
        fileName: item2.binary.data.fileName || 'Webtraining_Flyer.pdf',
        fileExtension: item2.binary.data.fileExtension || 'pdf',
        fileSize: item2.binary.data.fileSize || item2.binary.data.data.length
      }
    }
  }
];

